import { Injectable } from '@angular/core';

const componentsRoute: string[] = [
  'typography',
  'me-buttons',
  'me-text-box',
  'me-select-box',
  'me-check-box',
  'me-radio-group',
  'me-popup',
  'me-popover',
  'me-sidepage',
  'me-toolbar',
  'me-drop-down-button',
  'me-button-group',
  'me-list',
  'me-switch',
  'me-context-menu',
  'me-menu',
  'me-tree-view',
];

const componentsName: string[] = [
  'typography',
  'meButton',
  'meTextBox',
  'meSelectBox',
  'meCheckBox',
  'meRadioGroup',
  'mePopup',
  'mePopover',
  'meSidepage',
  'meToolbar',
  'meDropDownButton',
  'meButtonGroup',
  'meList',
  'meSwitch',
  'meContextMenu',
  'meMenu',
  'meTreeView',
];

@Injectable({
  providedIn: 'root',
})
export class ComponentRoutesService {
  getComponentsName() {
    return componentsName;
  }

  getComponentsRoute() {
    return componentsRoute;
  }
}

import { Injectable } from '@angular/core';

type Themes = 'light' | 'dark';

@Injectable({
  providedIn: 'root',
})
export class ThemesService {
  private _theme: Themes;

  constructor() {
    this._theme = <Themes>window.localStorage.getItem('monitel.designsystem.theme') || 'light';
  }

  get theme() {
    return this._theme;
  }

  set theme(value) {
    this._theme = value;
  }
}

