import { Directive, Input } from '@angular/core';
import { MeButtonStyle, MeButtonType, MeSize } from '../types/types';
import { MeButtonGroupItem } from '../types/types';

const LARGE_ICON_SIZE = '24';
const DEFAULT_ICON_SIZE = '20';

@Directive({
  selector: '[control]',
})
export class MeControlDirective {
  @Input() disabled: boolean = false;
  @Input() type: MeButtonType = 'normal';
  @Input() stylingMode: MeButtonStyle = 'contained';
  @Input() size: MeSize = 'medium';
  @Input() text: string = '';
  @Input({
    transform: (originalItems: MeButtonGroupItem[]) => structuredClone(originalItems),
  })
  items: MeButtonGroupItem[] = [];

  cloneItems(originalItems: MeButtonGroupItem[]) {
    return structuredClone(originalItems);
  }

  getIconSize(iconSize?: string) {
    let size = DEFAULT_ICON_SIZE;

    if (iconSize) {
      size = iconSize;
    } else if (this.size === 'large') {
      size = LARGE_ICON_SIZE;
    } else {
      size = DEFAULT_ICON_SIZE;
    }

    return size;
  }

  getText(index?: number) {
    if (typeof index === 'number' && this.items[index].text) {
      return `<span class="dx-button-text">${this.items[index].text}</span>`;
    }

    if (this.text) {
      return `<span class="dx-button-text">${this.text}</span>`;
    } else {
      return '';
    }
  }
}

import { Directive, ElementRef, Input, OnInit, Renderer2 } from '@angular/core';
import { MeControlDirective } from './control.directive';
import { MeIconStoreService } from '../service/icon-store.service';
import { DxDropDownButtonComponent } from 'devextreme-angular';
import { MeCommonType, MeScrollbarShowType } from '../types/types';

const DEFAULT_ICON_COLOR = '#ffffff';

@Directive({
  selector: '[meDropDownButton]',
})
export class MeDropDownButtonDirective extends MeControlDirective implements OnInit {
  @Input() icon: string = '';
  @Input() iconColor: string = '';
  @Input() iconSize: string = '';
  @Input() splitButton: boolean = false;
  @Input() wrapperAttr: MeCommonType = {};
  @Input() showScrollbar: MeScrollbarShowType = 'always';
  @Input() dropDownOptions: MeCommonType = {};

  constructor(
    private element: ElementRef,
    private component: DxDropDownButtonComponent,
    private renderer: Renderer2,
    private iconStore: MeIconStoreService,
  ) {
    super();
  }

  ngOnInit(): void {
    if (!this.iconColor) {
      if (this.type === 'normal' || this.stylingMode !== 'contained') {
        this.iconColor = `var(--button-${this.type}-icon-color)`;
      } else {
        this.iconColor = DEFAULT_ICON_COLOR;
      }

      if (this.disabled) {
        this.iconColor = `var(--button-${this.type}-${this.stylingMode}-icon-disabled-color)`;
      }
    }

    this.renderer.addClass(this.element.nativeElement, 'me-dropdownbutton');

    this.renderer.addClass(this.element.nativeElement, `me-dropdownbutton-${this.size}`);

    this.component.icon = this.iconStore.getIcon({
      icon: this.icon,
      color: this.iconColor,
      size: this.getIconSize(this.iconSize),
    });

    if (this.type === 'default') {
      this.renderer.addClass(this.element.nativeElement, 'dx-button-default');
    }

    if (this.splitButton) {
      this.renderer.addClass(this.element.nativeElement, 'me-split-button');
    }

    const popupWrapperClasses = `${
      this.wrapperAttr['class'] || ''
    } me-scroll-view me-dropdownlist-${this.size} me-dropdownlist ${
      this.showScrollbar === 'always' ? `me-scrollbar-visible` : ``
    }`;

    this.component.dropDownOptions = {
      wrapperAttr: {
        ...this.wrapperAttr,
        class: popupWrapperClasses,
      },
      ...this.dropDownOptions,
    };
  }
}

import { Directive, ElementRef, Inject, Input, OnDestroy, Renderer2 } from '@angular/core';
import { MeEditorComponents, MeSize } from '../types/types';
import { DxTextBoxComponent } from 'devextreme-angular';
import { BehaviorSubject, Subscription, debounceTime } from 'rxjs';

@Directive({
  selector: '[meEditor]',
  host: {
    '(keyup)': 'addFocus($event)',
    '(focusout)': 'removeFocus()',
  },
})
export class MeEditorDirective implements OnDestroy {
  @Input() size: MeSize = 'medium';
  focusSubject: BehaviorSubject<boolean>;
  focusSubscription: Subscription;

  constructor(
    protected element: ElementRef,
    @Inject(DxTextBoxComponent)
    protected component: MeEditorComponents,
    protected renderer: Renderer2,
  ) {
    this.focusSubject = new BehaviorSubject<boolean>(false);
    this.focusSubscription = this.focusSubject.pipe(debounceTime(0)).subscribe((isFocus) => {
      if (isFocus) {
        this.renderer.addClass(this.element.nativeElement, 'me-state-focus');
      } else {
        this.renderer.removeClass(this.element.nativeElement, 'me-state-focus');
      }
    });
  }

  initMeEditor() {
    this.renderer.addClass(this.element.nativeElement, 'me-editor');
    this.renderer.addClass(this.element.nativeElement, `me-editor-${this.size}`);

    this.component.elementAttr['size'] = this.size;
  }

  addFocus(event: KeyboardEvent) {
    if (event.key === 'Tab') {
      this.focusSubject.next(true);
    }
  }

  removeFocus() {
    this.focusSubject.next(false);
  }

  ngOnDestroy(): void {
    this.focusSubscription.unsubscribe();
  }
}

import { Directive, ElementRef, Input, OnInit, Renderer2 } from '@angular/core';
import { MeIconStoreService } from '../service/icon-store.service';

const DEFAULT_SIZE = 'auto';

@Directive({
  selector: '[meIcon]',
})
export class MeIconDirective implements OnInit {
  @Input() icon = '';
  @Input() color?: string;
  @Input() size?: string;
  @Input() height = '';
  @Input() width = '';

  constructor(
    private element: ElementRef,
    private service: MeIconStoreService,
    private renderer: Renderer2,
  ) {}

  ngOnInit(): void {
    const svg = this.service.getIcon({
      icon: this.icon,
      color: this.color,
      size: this.size,
    });
    this.element.nativeElement.innerHTML = svg;

    this.renderer.setStyle(
      this.element.nativeElement,
      'width',
      `${this.width ? this.width : DEFAULT_SIZE}px`,
    );

    this.renderer.setStyle(
      this.element.nativeElement,
      'height',
      `${this.height ? this.height : DEFAULT_SIZE}px`,
    );

    this.renderer.addClass(this.element.nativeElement, 'me-flex-centered');
  }
}

import {
  AfterContentChecked,
  AfterContentInit,
  ContentChild,
  Directive,
  ElementRef,
  Input,
  OnDestroy,
  OnInit,
  Renderer2,
} from '@angular/core';
import {
  DxCheckBoxComponent,
  DxSelectBoxComponent,
  DxSwitchComponent,
  DxTextBoxComponent,
} from 'devextreme-angular';
import { MeEditorComponents, MeLabelDirection } from '../types/types';

@Directive({
  selector: '[meLabel]',
})
export class MeLabelDirective implements OnInit, AfterContentInit, AfterContentChecked, OnDestroy {
  @ContentChild(DxTextBoxComponent) textBoxComponent?: DxTextBoxComponent;
  @ContentChild(DxSelectBoxComponent) selectBoxComponent?: DxSelectBoxComponent;
  @ContentChild(DxCheckBoxComponent) checkBoxComponent?: DxCheckBoxComponent;
  @ContentChild(DxSwitchComponent) switchComponent?: DxSwitchComponent;
  @Input() labelDirection: MeLabelDirection = 'row';
  @Input() width: string = '';
  field?: MeEditorComponents;
  isSwitch: boolean = false;
  unlistenLabel = () => { };

  constructor(private element: ElementRef, private renderer: Renderer2) { }

  ngOnInit(): void {
    this.renderer.addClass(this.element.nativeElement, 'dx-widget');
    this.renderer.addClass(this.element.nativeElement, 'me-label');

    if (this.width) {
      this.renderer.setStyle(this.element.nativeElement, 'width', this.width);
    }

    if (this.labelDirection === 'column') {
      this.renderer.addClass(this.element.nativeElement, 'me-flex-column');
    }

    if (this.labelDirection === 'row') {
      this.renderer.addClass(this.element.nativeElement, 'me-flex-row');
    }
  }

  ngAfterContentInit(): void {
    this.field = this.textBoxComponent;
    this.field ||= this.selectBoxComponent;
    this.field ||= this.checkBoxComponent;
    this.field ||= this.switchComponent;

    this.unlistenLabel = this.renderer.listen(
      this.element?.nativeElement,
      'click',
      this.onLabelClick,
    );

    let size = this.field?.elementAttr?.size;

    if (size?.includes('large') && this.labelDirection === 'column')
      this.renderer.addClass(this.element.nativeElement, 'me-label-large');

    if (this.labelDirection === 'row') {
      if (size.includes('small'))
        this.renderer.addClass(this.element.nativeElement, 'me-label-row-small');
      if (size.includes('medium'))
        this.renderer.addClass(this.element.nativeElement, 'me-label-row-medium');
      if (size.includes('large'))
        this.renderer.addClass(this.element.nativeElement, 'me-label-row-large');
    }
  }

  ngAfterContentChecked(): void {
    if (!this.field?.isValid) {
      this.renderer.addClass(this.element.nativeElement, 'me-label-invalid');
    }
    if (this.field?.isValid) {
      this.renderer.removeClass(this.element.nativeElement, 'me-label-invalid');
    }

    this.isSwitch = this.field instanceof DxSwitchComponent;

    if (this.isSwitch) {
      if (this.field?.disabled) {
        this.renderer.addClass(this.element.nativeElement, 'me-label-disabled');
      } else {
        this.renderer.removeClass(this.element.nativeElement, 'me-label-disabled');
      }

      if (!this.field?.disabled && !this.field?.readOnly) {
        this.renderer.addClass(this.element.nativeElement, 'me-label-container');
      }
    }
  }

  ngOnDestroy(): void {
    this.unlistenLabel();
  }

  onLabelClick = (e: Event) => {
    if ((e.target as HTMLElement).classList.contains('me-label-container')) {
      if (this.field) {
        const instance = this.field.instance;
        instance.focus();

        if (this.isSwitch && !this.field?.disabled && !this.field?.readOnly) {
          instance.option({ value: !instance.option().value });
        }
      }
    }
  };
}

