  styleUrls: ['./me-toolbar.component.css'],
})
export class MeToolbarComponent {
  fontSizes = fontSizes;
  lineHeights = lineHeights;
  lineHeight = this.lineHeights[1].lineHeight;
  fontFamilies = fontFamilies;
  fontStyles = fontStyles;
  textAlignItems = this.getTextAlign();
  textAlignItemsExtended = textAlignsExtended;
  selectedTextAlign = [this.textAlignItems[0].alignment];
  listTypes = listTypes;
  headings = headings;
  heading = this.headings[0].text;

  textBoxOptions = {
    placeholder: 'Search...',
    showClearButton: true,
  };

  searchButtonOptions = {
    icon: 'search',
    onClick() {},
  };

  constructor() {}

  getTextAlign(): TextAlign[] {
    return textAlignsExtended.map(({ icon, alignment, hint }) => ({
      icon,
      alignment,
      hint,
    }));
  }
}

styleUrlsData.data:
::ng-deep .dx-resizable-handle::after {
  content: '';
  position: absolute;
  width: 9px;
  height: 36px;
  border: none;
  border-radius: 50px;
  background-color: #fff;
  box-shadow: 0 2px 6px 0 rgba(0, 0, 0, 0.24);
}

::ng-deep .dx-resizable-handle-right::after {
  top: 50%;
  right: -5px;
  transform: translateY(-50%);
}

::ng-deep .resizable-container {
  padding: 10px;
  height: 300px;
  border: 1px dotted #999;
  border-radius: 4px;
  box-sizing: border-box;
  width: 800px;
}

.flex-column {
  gap: 50px;
  margin: 30px 0px;
}

