};

export const WithWeekNumbers: Story = {
  args: {
    ...Default.args,
    showWeekNumbers: true,
  },
};

export const DifferentZoomLevels: Story = {
  args: {
    ...Default.args,
    zoomLevel: 'year',
  },
};

export const CustomWeekNumberRule: Story = {
  args: {
    ...Default.args,
    weekNumberRule: 'fullWeek',
  },
};

export const Disabled: Story = {
  args: {
    ...Default.args,
    disabled: true,
  },
};

import { moduleMetadata, type Meta, type StoryObj } from '@storybook/angular';
import { DxButtonModule, DxLoadPanelModule, DxCheckBoxModule } from 'devextreme-angular';
import {MeLoadPanelDirective} from "../app/directives/load-panel.directive";
import { Component, Input } from '@angular/core';


@Component({
  selector: 'load-panel-demo',
  template: `
    <dx-button text="Load Data" (onClick)="showLoadPanel()"> </dx-button>

    <dx-load-panel
      #loadPanel
      meLoadPanel
      shadingColor="rgba(0,0,0,0.4)"
      [position]="{ of: '#employee' }"
      [(visible)]="loadingVisible"
      [showIndicator]="showIndicator"
      [showPane]="showPane"
      [shading]="shading"
      [hideOnOutsideClick]="hideOnOutsideClick"
      (onShown)="onShown()"
      (onHidden)="onHidden()"
      [size]="size"
      [customClass]="customClass"
    ></dx-load-panel>
  `,
  styles: [`
    .header {
      font-size: 24px;
      margin-bottom: 20px;
    }
    #employee {
      margin: 20px 0;
    }
    .options {
      margin-top: 20px;
    }
    .option {
      margin-bottom: 10px;
