            [maxWidth]="maxWidth"
            [showTitle]="showTitle"
            [title]="title"
            [shading]="shading"
            [shadingColor]="shadingColor"
          >
            <dxo-animation *ngIf="animationEnabled">
              <dxo-show type="pop" [from]="{ scale: 0 }" [to]="{ scale: 1 }"></dxo-show>
              <dxo-hide type="fade" [from]="1" [to]="0"></dxo-hide>
