  display: flex;
  justify-content: space-around;
  padding: 20px;
  background-color: #f0f0f0;
  border-radius: 10px;
}

.tooltip-group {
  text-align: center;
  margin-left: 20px;
}

.tooltip-controls {
  display: flex;
  flex-direction: column;
  gap: 15px;
}

.tooltip-button {
  padding: 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s;
}

.interactive-tooltip {
  box-sizing: border-box;
  padding: 4px;
}

.interactive-tooltip * {
  text-wrap: balance;
}

.interactive-tooltip button {
  margin-top: 8px;
  margin-inline: 4px;
  padding: 4px 8px;
  border: none;
  border-radius: 3px;
  cursor: pointer;
}

.interactive-tooltip .accept-button {
  background-color: #3257dc;
  color: white;
}

.interactive-tooltip .decline-button {
  background-color: #ecedf3;
  color: #18181a;
}

.custom-tooltip {
  background-color: #ff9800;
  color: white;
  border: 2px solid #e68a00;
  border-radius: 10px;
  padding: 10px;
}

.html-tooltip * {
  text-wrap: balance;
  text-align: start;
  margin-top: 4px;
}

